import React, {Component}  from 'react';
import {Link, Route} from "react-router-dom";

export const HeroesArray = [
    {
        id: 0,
        name: 'Leonardo DiCaprio',
        as: 'Dom Cobb',
        src: 'Leonardo_DiCaprio.jpg',
        decs: 'A man in a black suit, a woman in a pink dress, a man in a plaid suit, a woman in a black dress, a Japanese man in a black suit, and an old man in a blue suit clap their hands, while a man in a black suit stands. A microphone stand is in the foreground, and blue curtains are in the background.\n' +
            'The cast at a premiere for the film in July 2010: From left to right: Cillian Murphy, Marion Cotillard, Joseph Gordon-Levitt, Ellen Page, Ken Watanabe, Michael Caine, and Leonardo DiCaprio\n' +
            'Leonardo DiCaprio as Dom Cobb, a professional thief who specializes in conning secrets from his victims by infiltrating their dreams. DiCaprio was the first actor to be cast in the film.[13] Nolan had been trying to work with the actor for years and met him several times, but was unable to convince him to appear in any of his films until Inception.[14] Both Brad Pitt and Will Smith were offered the role, according to The Hollywood Reporter.[15] Cobb\'s role is compared to "the haunted widower in a Gothic romance".[16]'
    },
    {
        id: 1,
        name: 'Arthur',
        src: 'Joseph_Gordon.jpg'
    },

    {
        id: 2,
        name: 'Ariadne',
        src: 'Ellen_Page.jpg'
    },

    {
        id: 3,
        name: 'Eames',
        src: 'Tom_Hardy.jpg'
    },

    {
        id: 4,
        name: 'Mr.Saito',
        src: 'Ken_Watanabe.jpg'
    },

    {
        id: 5,
        name: 'Yusuf',
        src: 'Dileep_Rao.jpg'
    },

    {
        id: 6,
        name: 'Robert Michael Fischer',
        src: 'Cillian_Murphy.jpg'
    },

    {
        id: 7,
        name: 'Peter Browning',
        src: 'Tom_Berenger.jpg'
    },

    {
        id: 8,
        name: 'Mal Cobb',
        src: 'Marion_Cotillard.jpg'
    },

    {
        id: 9,
        name: 'Maurice Fischer',
        src: 'Pete_Postlethwaite.jpg'
    },

    {
        id: 10,
        name: 'Professor Stephen Miles',
        src: 'Michael_Caine.jpg'
    },

    {
        id: 11,
        name: 'Nash',
        src: 'Lukas_Haas.jpg'
    },

    {
        id: 12,
        name: 'woman whom Eames disguises himself as in a dream',
        src: 'Talulah_Riley.jpg'
    },
];

const Hero = ({ match }) => {
    return(
        <div className="personsDesc">
            <div className="label">{match.params.name} as {match.params.as}</div>
        </div>
    );
};


class Heroes extends Component {
    render() {
        return(
            <div className="content">
                <div className="label">Cast</div>
                <div
                    className="heroesList"
                    style={{
                        flexWrap: 'wrap',
                        justifyContent: 'center',
                        marginTop: '60px',
                    }}
                >
                    {
                        HeroesArray.map(hero =>  {
                            let bg = require(`../images/heroes/${hero.src}`);
                            return(
                                <Link to={`${this.props.match.url}/${hero.name}`} key={hero.id}>
                                    <div
                                        className="heroImg"
                                        style={{
                                            background: `url(${bg})`,
                                            marginBottom: '10px',
                                        }}
                                    >
                                    </div>
                                </Link>
                            )
                        })
                    }
                </div>

                <Route path={`${this.props.match.path}/:name`} component={Hero} />
                <Route
                exact
                path={this.props.match.path}
                render={() => <h3>Please select a topic.</h3>}
                />
            </div>
        );
    }

}

export default Heroes;
