import React, {Component}  from 'react';
import { Link } from "react-router-dom";

const ScenesArray = [
    {
        id: 0,
        name: 'name',
        src: '01.jpg',
    },
    {
        id: 1,
        name: 'name',
        src: '02.jpg',
    },
    {
